import React, { useContext } from 'react';
import { AuthContext } from '../../context/AuthProvider';
import { Button } from "@material-ui/core";
import { PhotoCamera } from "@material-ui/icons";
import { useState } from 'react';
import { firebaseStorage, firebaseDb } from '../../config/firebase';
import { uuid } from 'uuidv4';
import { useEffect } from 'react';

const Feeds = (props) => {
    const { currentUser } = useContext(AuthContext);
    const { signOut } = useContext(AuthContext);
    const [videoFile, setVideoFile] = useState(null);
    const handleLogOut = async (e) => {
        try {
            await signOut();
            props.history.push("/login");
        } catch (err) {
            console.log(err);
        }
    }

    const handleInputFile = (e) => {
        let file = e.target.files[0];
        setVideoFile(file);
    }

    const handleUploadFile = async () => {
        try {
            // upload video in firebase storage
            let uid = currentUser.uid;
            const uploadVideoObject = firebaseStorage
                .ref(`/profilePhotos/${uid}/${Date.now()}.mp4`)
                .put(videoFile);
            uploadVideoObject.on("state_changed", fun1, fun2, fun3);
            function fun1(snapshot) {
                // bytes transferred
                // totoal bytes
                let progress = (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
                console.log(progress);
            }
            // if indicates a error !!
            function fun2(error) {
                console.log(error);
            }
            // it indicates success of the upload !!
            async function fun3() {
                let videoUrl = await uploadVideoObject.snapshot.ref.getDownloadURL();
                console.log(videoUrl);
                let pid = uuid(); // unique id
                await firebaseDb.collection("posts").doc(pid).set({
                    pid: pid,
                    uid: uid,
                    comments: [],
                    likes: [],
                    videoLink: videoUrl
                    // createdAt: timeStamp(),
                });
                let d = await firebaseDb.collection("users").doc(uid).get();
                let document = d.data();
                console.log(document);
                document.postsCreated.push(pid);
                await firebaseDb.collection("users").doc(uid).set(document);
                // setUploadVideoError("");
            }
        } catch (err) { }
    };

    useEffect(() => {
        firebaseDb.collection("posts").get().then( snapshot => {
            snapshot.docs.map( doc => {
                console.log(doc.data());
            })
        })
    }, []);

    return (
        <div>
            <h1>Feeds</h1>
            <div>
                <button onClick={handleLogOut}> Log Out </button>
            </div>
            <div className="uploadVideo">
                <div>
                    <input type="file" onChange={handleInputFile} />
                    <label>
                        <Button onClick={handleUploadFile} variant="contained" color="secondary" startIcon={<PhotoCamera></PhotoCamera>}> Upload </Button>
                    </label>
                </div>
            </div>
            <div className="feeds-video-list">

            </div>
        </div>
    );
}

export default Feeds;
