import React, { useContext, useState } from 'react';
import { firebaseStorage, firebaseDb } from '../../config/firebase';
import { AuthContext } from '../../context/AuthProvider';


const Signup = () => {

    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [userName, setUserName] = useState("");
    const [dp, setdp] = useState(null);
    const [message, setMessage] = useState("");
    const { signUp } = useContext(AuthContext)

    const handleFileSumbit = (event) => {
        let fileObj = event.target.files[0];
        setdp(fileObj);
    }

    const handleSignUp = async () => {
        try {
            let response = await signUp(email, password);
            let uid = response.user.uid
            const uploadPhotoObject = firebaseStorage
                .ref(`/profilePhotos/${uid}/image.jpg`)
                .put(profileImage);
            uploadPhotoObject.on("state_changed", fun1, fun2, fun3);
            function fun1(snapshot) {
                let progress = (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
                console.log(progress);
            }

            function fun2(error) {
                console.log(error);
            }

            async function fun3() {
                let profileImageUrl =
                    await uploadPhotoObject.snapshot.ref.getDownloadURL();

                firebaseDB.collection("users").doc(uid).set({
                    email: email,
                    userId: uid,
                    username: username,
                    profileImageUrl: profileImageUrl,
                    postsCreated: []
                });
                props.history.push("/");
            }
        } catch (err) {
            setMessage(err.message);
        }
    };

    return (
        <div>
            <h1>Sign up</h1>
            <div>
                <div>
                    Email <input value={email} onChange={(e) => setEmail(e.target.value)}></input>
                </div>
                <div>
                    Password <input value={password} onChange={(e) => setPassword(e.target.value)}></input>
                </div>
                <div>
                    User-Name <input value={userName} onChange={(e) => setUserName(e.target.value)}></input>
                </div>
                <div>
                    Profile-Image
                    <input type="file" accept="image/*" onChange={(e) => handleFileSumbit(e)}></input>
                </div>
                <button onClick={handleSignUp}> Sign-Up </button>
                <h1>{message}</h1>
            </div>
        </div>
    );
}

export default Signup;
