import React, { useContext, useState } from 'react';
import { AuthContext } from '../../context/AuthProvider';
import {TextField, Grid, Button, Paper, Card, CardContent, Container, CardMedia, Typography, makeStyles, CardActions} from "@material-ui/core"
import { Link } from 'react-router-dom';

const Login = (props) => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [message, setMessage] = useState("");
    let {login} = useContext(AuthContext);

    const handleLogin = async (e) => {
        try {
            let response = await login(email, password);
            props.history.push("/");
        } catch(err) {
            setEmail("");
            setPassword("");
            setMessage(err.message);
        }
    }

    return (
        <div>
            {/* <h1> Login Page </h1>
            <div>
                Email <input value={email} onChange={(e) => setEmail(e.target.value)}></input>
            </div>
            <div>
                Password <input value={password} onChange={(e) => setPassword(e.target.value)}></input>
            </div>
            <div>
                <button onClick={handleLogin}>Login</button>
                <h1>{message}</h1>
            </div> */}
            <Container>
                <Grid container spacing={2}>
                    <Grid item sm={5}>
                        <Paper> Carseoul </Paper>
                    </Grid>
                    <Grid item sm={5}>
                        <Card variant="outlined">
                            <CardMedia>Logo</CardMedia>
                            <CardContent>
                                <TextField label="Email" type="email" variant="outlined" value={email} size="small" onChange={(e) => setEmail(e.target.value)}></TextField>
                            </CardContent>
                            <CardContent>
                                <TextField label="password" type="password" variant="outlined" value={password} size="small" onChange={(e) => setPassword(e.target.value)}></TextField>
                            </CardContent>
                            <CardActions>
                                <Button variant="contained" color="primary" onClick={handleLogin}> Login </Button>
                            </CardActions>
                        </Card>
                        <Card variant="outlined">
                            <Typography>
                                Don"t have an account ? 
                                <Link to="/sign">SignUp</Link>
                            </Typography>
                        </Card>
                    </Grid>
                </Grid>
            </Container>
        </div>
    )

}

export default Login;
